import React, { Component } from 'react'
import axios from 'axios'



const Blogs =({image, title, body, created})=>{

    return (
        
        <div className="ui main text container">
            <div className="ui huge header">{title}</div>
         <div className="ui top attached segment">
             <div className="item">
                <img className="ui centered rounded image" src={image} alt="blog" />
                 <div className="content">
                     <div className="meta">
                         <span>{created}</span>
                     </div>
                      <div className="description">
                         <p>{body}</p>
                      </div>
                 </div>
             </div>

         </div>
         </div>


    )  
}



export default class ShowBlogs extends Component{
constructor(props){
    super();

    this.state={
        blogs:[]
    }
  

   }

   componentDidMount(){ 
    axios.get(`http://localhost:3031/blogs/${this.props.match.params-id}`)
    .then(response => this.setState({blogs:response.data}))
    .catch(err => console.error(err.message))
    }
   

    render(){

        return(
            
            <>  
            <h1>Blogs</h1>
            {this.state.blogs.map(blog =>{ 
                return(<Blogs
                    key={blog._id}
                    id={blog._id}
                    title={blog.title}
                    image={blog.image}
                    body={blog.body}
                    created={blog.created}
                   />)
                 })
                }      
             </>
        )
    }

}
