export default class CreateBlog extends Component{
    constructor(props){
      super(props)
      this.state = {
          date: new Date(),
          title: '', 
          body: '',
          user:[]
      }
    }

    onChangeTitle(e){
        this.setState({title: e.target.value})
    }
    onChangeBody(e){
        this.setState({body: e.target.value})
    }
    onChangeUser(e){
        this.setState({user: e.target.value})
    }
